sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading && !g_form.isNewRecord() || newValue === '') {
        return;
    }
// alert('test');
	var test = isLoading;
	g_form.addInfoMessage(test);
    var getUser = new GlideAjax('getEmailAddress');
    getUser.addParam('sysparm_name', 'getEmail');
    getUser.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
    getUser.getXML(populateEmail);

    function populateEmail(response) {
        var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('u_requested_for_email');
        g_form.setValue('u_requested_for_email', emailFromScriptInclude);
    }

}
